mapper.insert:
INSERT INTO TB_COMPLAIN_SSONG(
ID
,TITLE
,CONTENT
,TARGET_PAGE
,WRITER
,INSERT_TIME
) VALUES (
SQ_TB_COMPLAIN_SSONG.NEXTVAL
,#{title}
,#{content}
,#{targetPage}
,#{writer}
,TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
)


    SELECT SQ_TB_COMPLAIN_SSONG.CURRVAL FROM DUAL

mapper.update:
UPDATE TB_COMPLAIN_SSONG
SET TITLE       = #{title}
  , CONTENT     = #{content}
  , TARGET_PAGE = #{targetPage}
  , WRITER      = #{writer}
  , UPDATE_TIME = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
WHERE ID = #{id}
